css:
@-moz-document domain("www.cricbuzz.com") {
body {
    background: #1f1f1f;
    color: #bbb;
}
.cb-hm-scg-blk {
    color: #bbb;
    background: #2f2f2f;
}
.cb-lst-itm, .cb-bg-white {
    background-color: #212121;
    color: #bbb;
}
.cb-nws-hdln-ancr {
    color: #c1c1c1;
}
a {
    color: #c1c1c1;
}
.cb-expt-cmnt-wrp {
    background: #1f1e1e;
    border: 1px solid #444242;
}
.cb-bg-gray {
    background: #424141;
}

.cb-min-hdr-rw, .cb-scrd-sub-hdr {
    color: #989898;
}
.cb-key-lst-wrp {
    border: 1px solid #3c3c3c;
}
.cb-lv-scrs-well {
    border: 1px solid #545454;
}
.cb-lv-scrs-well {
    background: #1b1b1b;
}
.cb-hmscg-bat-txt {
    color: #b3b3b3;
}
.cb-plyr-inp-new {
    background-color: #232323;
    border: 1px solid #4e4141;
}
.cb-lv-grn-strip {
    background: #272727;
}
.cb-tms-itm {
    border-top: 1px solid #1b1b1b;
}
.cb-com-ovr-sum {
    background: #292929;
}
.cb-hm-mid {
    border-left: 5px solid #313131;
    border-right: 5px solid #313131;
}
.cb-nav {
    background: #00503e;
}
.cb-lv-scrs-well:hover {
    background: #191919;
}
}
