css:
@-mox-document domain(slatestarcodex.com) {
html {
  filter: invert(90%);
  background: rgb(26,26,26);
}
* {
  box-shadow: none !important;
  text-shadow: none !important;
}
img {
  filter: invert(100%);
}
#pjgm-header {
  /*background: linear-gradient(rgb(116, 84, 23) 0%, rgb(176, 140, 62) 100%);*/
  filter: invert(100%);
  background: linear-gradient(rgba(139, 171, 232, 0.75) 0%, rgba(79, 115, 193, 0.75) 100%);
}
#pjgm-header img {
  filter: invert(0);
}
#pjgm-main a {
  color: #ffaa33;
}
#pjgm-main {
  border-bottom-left-radius: 20px;
  border-bottom-right-radius: 20px;
  overflow: hidden;
}
#pjgm-footer {
  display: none;
}
}
