css:
@-moz-document domain("charlieintel.com") {
.td-outer-container {
    background-color: rgba(18, 18, 18, .90);
}

.td-ss-main-content, .td-post-template-5 header, .td-pb-row [class*="td-pb-span"], .td-pb-row, .td-container {
    background-color: #212121;
    border-color: #212121;
}

.td-related-title .td-related-right, .textarea, .post-actions, .input--text {
    background-color: #121212;
}

p, .td-related-title .td-related-right, .post-content .post-message p {
    color: #E0E0E0;
}

.post header h1, .td-module-title a, .td-post-author-name a, .textarea {
    color: #FFF;
}

.meta-info, .td-post-comments a {
    color: #999;
}

.td-post-template-5 .td-post-header .meta-info, .td-next-prev-wrap i, .td-post-sharing, .td-header-style-9 .td-ad-m, #disqus_thread {
    border-color: #121212;
}

.post-actions, .textarea-wrapper, .input--text, .td-add-border {
    border-color: #212121;
}
}
