css:
@-moz-document domain("lwn.net") {
html body {
    line-height: 1.4;
    font-size: 18px;
    margin-left: 5em;
    background: #DFDFD0;
}
body DIV.ArticleText{
    padding: 30px;
    text-align: justify;
}
body #menu{
    width: 12em;
    background-color: rgba(0,0,0,0.2);
    border-top: 31px solid #555;
    border-bottom: 1px solid rgba(0,0,0,.2);
    padding: 20px 0 0 20px;
}
body .navmenu ul{
    background:none;
}
body DIV.GAByline{
	margin: 1em 0 1em 1em;
}
body .lwn-u-1{
    background: rgba(255,255,255,.3);
    box-shadow: 0px 0px 8px 0px rgba(0,0,0,.2);
    border-radius: 2px;
    margin-top: 40px;
    max-width: 960px;
    width:auto;
    margin-right:2em;
}
body DIV.PageHeadline {
    text-align: center;
    max-width: 75em;
    margin-top: 0;
    margin-bottom: 0em;
    border-bottom: 2px dashed #ccc;
    line-height: 3;
}
body DIV.topnav-container {
    margin: 0;
    text-align: right;
    border-radius: 0;
    font-size: 12px;
    color: rgba(0,0,0,.5);
    font-weight: lighter;
    border-bottom: 1px solid rgba(0,0,0,.2);
    height: 30px;
    padding: 0 10px;
    line-height: 30px;
}
body .lwn-u-1.pure-u-md-1-6{
    width:0;
    display:none;
}
body form.loginform input {
    border: 1px solid rgba(0,0,0,.2);
    background-image: none !important;
}
body a.navmenu{
    color: #999;
}
body a:link{
	color:#374;
}
body a:hover{
    background: none;
}
body .navmenu ul li{
    text-indent:0;
}
}
