css:
@-moz-document domain("warnet.ws")

{
.layout {background-color: #3c3c3c;}

.page {background-color: #868686;}

.ls-activity-block-recent-user {
    color: #d49292;
    text-decoration: underline;
    margin-right: 5px;
    text-shadow: 0 0 3px black;}

.ls-nav--pills > .ls-nav-item > .ls-nav-item-link {color: #f88a18;}

.ls-topic-info-item--views-count {color: #f88a18;}

.ls-topic-header .ls-topic-info {
    color: #f88a18;
    text-shadow: 0 0 4px black;}

.ls-topic-header .ls-topic-info-item--blog a {color: #f88a18;}

.ls-tag-cloud-item a {color: #f88a18;}

.ls-topic-info-item.ls-topic-info-item--comments a {
    text-decoration: none;
    color: #a3e4f9;}

.ls-activity-block-recent-date {color: #f88a18;}

.ls-avatar--has-name.ls-avatar--inline .ls-avatar-name-link { 
    color: #d49292;
    text-decoration: underline;
    margin-right: 5px;
    text-shadow: 0 0 3px black}

.ls-activity-block-recent-comments {
    margin-left: 5px;
    font-size: 11px;
    text-decoration: none;
    color: #a3e4f2;}

.page {background-color: #565656;}

.ls-topic-title a {
    color: #000;
    text-shadow: 0 0 3px #f88a18;}

.blog {background-color: #565656;}

.ls-block {background-color: #565656;}

article.ls-topic {background-color: #565656;}

.ls-block-live__item-post {color: #5b9fcc;}

.ls-topic-header .ls-topic-info-item--blog a {color: #a2e2f8;}
.sbibod {background-color: #fc8a17;}
.ls-tags-item {color: #fc8a17;}
.ls-comment {background: #565656;}
.ls-comments-header {padding: 4px;}
.ls-actionbar {
    padding: 0px;
    margin-bottom: 0px;}

.ls-block-live__item-post {
    color: #fc8a17;
    Font-size: 100%;}
.ls-toolbar-item {
    background: #565656;
    color: #adadad;
    box-shadow: 0 0 8px rgba(0, 0, 0, 0.52);}
}
