css:
@-moz-document domain(cloud.google.com) {




@media screen and (max-width:3000px) {
 .devsite-section-nav {
  display:none
 }
}
@media screen and (max-width:3000px) {
 .devsite-expand-section-nav {
  display:inline-block;
  margin:12px 16px 12px 0
 }
}
@media screen and (max-width:3000px) {
 .devsite-rating-container {
  display:none
 }
}


.devsite-top-section {
 background-color:#039be5;
 box-shadow:0 0 4px rgba(0,0,0,.14),0 4px 8px rgba(0,0,0,.28);
 position: absolute;
 top:0;
 width:calc(100%);
 z-index:10005
}

.devsite-top-logo-row {
 display:-ms-flexbox;
 display:flex;
 height:48px;
 padding:0 24px;
 position: absolute
}

 .devsite-header-upper-tabs .devsite-doc-set-nav,.devsite-feedback-button {
  display:none
 }

 .devsite-site-logo+.devsite-site-name {
  display:none
 }

 .devsite-site-logo {
  display:none
 }

@media screen and (max-width:3000px) {
 .devsite-product-id-row,.devsite-header-no-lower-tabs .devsite-product-id-row {
  display:none;
  padding:16px
  }
.devsite-article-inner {
 float: none;
 width:calc(100%-200px)
}
}

 .devsite-product-button-row {
  margin:16px 0 0;
  text-align: end;
   display: none;
 }

.devsite-article {
 margin-left:50px;
 position:relative;
 width:calc(100% - 300px)
}

@media screen and (max-width:1201px) {
 .devsite-has-apix .devsite-article {
  width:calc(100% - 0px);
  margin-left:50px
 }
}
.devsite-article-inner {
 float:left;
 width:100%
}

.devsite-section-nav+.devsite-article,.devsite-section-nav+.devsite-page-nav+.devsite-article {
  margin-left:50px
 }
}
