css:
@-moz-document domain('overleaf.com'){

pre.CodeMirror-line {
    font-family: "Times-New-Roman","Liberation Serif", serif;
    font-size: 1.2em;
}

pre.CodeMirror-line .cm-tag,
pre.CodeMirror-line .cm-comment,
pre.CodeMirror-line .cm-keyword,
pre.CodeMirror-line .cm-string{
    font-family: Menlo, Monaco, Consolas, "Courier New", monospace;
    font-size: 0.8em;
}

.CodeMirror pre {
    padding: 0 4px 0 0;
}
  
}
