css:
@-moz-document domain("framaboard.org") {

.table-list-row .table-list-details {
	font-weight: normal;
    color: #666;
}

.sidebar > ul a {
	font-weight: normal;
    color: #666;
}

.task-list-avatars .task-avatar-assignee {
	font-weight: normal;
	color: #000;
}

.table-list-header {
 	border-color: black;   
}

.table-list-row, .table-list-row.table-border-left {
 	border-color: black;   
}

.input-addon-field {
    color: #333;
    border-color: black;
}

.panel {

    border-color: black;

}

.views li {
    padding: 10px 15px;
}

.sidebar > ul a {
    font-weight: initial;
    color: black;
}

.sidebar > ul li.active a {
    color: black;
    font-size: 110%;
}

td a.dropdown-menu i {
    color: #333;
}

.draggable-row-handle {
    cursor: move;
    color: #333;
}
  
}
