css:
@-moz-document regexp("https?://(?!(www.your.sites.here.com|forum.example.com)).*"), url-prefix(ftp://), url-prefix(file://), url-prefix(about:), url-prefix(javascript) {
  input:not(.urlbar-input):not(.textbox-input):not(.form-control):not([type='checkbox']):not([type='radio']) {
      -moz-appearance: none !important;
      background-color: white;
      background-color: inherit;
      color: black;
      color: inherit;
  }

  #downloads-indicator-counter {
      color: white;
      color: inherit;
  }

  textarea {
      -moz-appearance: none !important;
      background-color: white;
      background-color: inherit;
      color: black;
      color: inherit;
  }

  select {
      -moz-appearance: none !important;
      background-color: white;
      background-color: inherit;
      color: black;
      color: inherit;
  }
}
