css:
@-moz-document domain("dbwebb.se") {
/* Light: #1e2123 */
/* Dark: #121415 */
/* Text Light: #eeeeee */
/* Text Dark: #b7b7b7 */

body .rm-navbar-top .rm-desktop a,
.article .block h4 a,
.columns-above .block h4 a,
.columns-below .block h4 a,
.sidebar .block h4 a,
body .rm-navbar-top .rm-desktop,
.article .block h4,
.columns-above .block h4,
.columns-below .block h4,
.sidebar .block h4,
body {
  background: #121415 none;
  color: #eeeeee;
}

body .rm-navbar-top .rm-desktop a {
  border: none;
}

.article .block h4 a,
.columns-above .block h4 a,
.columns-below .block h4 a,
.sidebar .block h4 a,
.article .block h4,
.columns-above .block h4,
.columns-below .block h4,
.sidebar .block h4 {
  border: none;
  border-bottom: 1px solid #eeeeee;
  text-decoration: none;
  text-shadow: none;
  color: #eeeeee;
  transition: color .2s linear;
}

.article .block h4 a:hover, .columns-above .block h4 a:hover, .columns-below .block h4 a:hover, .sidebar .block h4 a:hover {
  color: #b7b7b7;
  text-shadow: none;
  text-decoration: none;
}

.rm-navbar-top .rm-desktop a:hover,
.rm-navbar-top .rm-desktop li.selected a {
  color: #b7b7b7;
  text-shadow: none;
  text-decoration: none;
}

.rm-navbar-top .rm-desktop a {
  transition: color .2s linear;
}

* a, * a:hover {
  color: #5454ef;
}

* a:visited, * a:hover:visited {
  color: #c5c;
}

.site-logo-text > a,
.site-logo-text > a:visited {
  color: #eeeeee;
}


.site-logo-text > a:hover,
.site-logo-text > a:hover:visited {
  color: #b7b7b7;
}

.site-logo-text,
.site-logo-text a:hover {
  text-shadow: none;
}

body code {
	background-color: #1e2123;
  border-color: #444444;
}

body pre.hljs {
  color: #eeeeee;
	background: #1e2123 none;
}

.hljs-deletion,
.hljs-number,
.hljs-quote,
.hljs-selector-class,
.hljs-selector-id,
.hljs-string,
.hljs-template-tag,
.hljs-type {
    color: #a6e22e;
}

}
