css:
@-moz-document url("https://www.minuddannelse.net/Node/") {
.panel-group{
    margin-right: -140px;
    margin-left: 250px;
    -webkit-transform:translateY(-500px);
}
.panel-body{
    position:fixed; /* absolute */
    display: block;
    padding-right: 50px;
    margin-top:50px;
    margin-left:-300px;
    margin-right:-300px;
    /*position: fixed; or absolute */
    background-color: white;
    transform: translateX(-77%);
    z-index: 10;
}
.panel-heading{
    padding-right:10px;
    margin:0;
    transition-delay: 0.1s;
}
.panel-heading:hover{
    margin-left:10px;
    transition-delay:0s;
}
}
