css:
@-moz-document domain('forum-alternative-antriebe.de')
{
/* HEADER LINKS */
#main_menu .linklevel1:link, #main_menu .linklevel1:visited {
 	color: black;   
    border: 1px solid black;
}

#main_menu .linklevel1:hover, #main_menu .linklevel1:link:hover, #main_menu .linklevel1:visited:hover {
 	color: #0E155F;   
    border: 1px solid #0E155F;
}

#main_menu .linklevel1.active, #main_menu .listlevel1:hover .active {
 	background-color: #529DCC !important;
    color: white !important;
    border: 1px solid #0E155F;
}

/* Gr√ºn */
.linktree:last-child, .linktree:last-child::after {
 	background-image: none;
    background-color: #529DCC;
    background: #529DCC !important;
}
.linktree:hover, .linktree:last-child {
    background-color: #0E155F;
}

.new_posts, .new_posts:visited, .new_posts:link {
 	background: #0E155F;
}
}
