css:
@-moz-document domain("hwp.ru")
{

.scroller, .scroller-inner {
    position: relative;
    background-color: #989898;}

.editor p {
    color: #0d2726;}

.article p {
    color: #000000;
    font-size: 1.25em;
    font-weight: 400;
    margin-left: 0.4em;
    line-height: 1.5em;
    margin-top: 1.5em;
    margin-right: 0.4em;
    text-shadow: 0 0 2px #3cc8c4;}

.container {
    width: 100% !important;}

.print-text p {
    color: #282828;
    font-size: 1.25em;
    font-weight: 400;
    line-height: 1.5em;
    font-style: italic;
    margin: 0.5em 0;
    text-shadow: 0 0 1px #3cc8c4;}

}
