css:
@-moz-document url-prefix("https://www.rabb.it/s/") {
.chat .body .chatFadeOverlay {
    background: black;
}
.chat .body .chatFadeOverlay .typingActivity {
    color: royalblue;
    background: black;
}
.spacePane {
    background: black;
    border-left: 1px solid royalblue;
}
.chat .body .message.me .messageBody {
    color: royalblue;
    background: black;
    border: 1px solid royalblue;
}
.chat .body .message .messageBody {
    color: royalblue;
    background: black;
    border: 1px solid royalblue;
}
.chat .body .message header .userName {
    color: royalblue;
}
.spacesView .spaces > .tray {
    background: black;
    color: royalblue;
}
.chat > footer textarea:focus {
    border: 1px solid royalblue;
    color: royalblue;
}
.spacesView .spaces:not(.room) .tray .list > .space.selected {
    background: #222;
}
.spacesView .spaces:not(.room) .tray .list > .space > .textContainer > .topLine > .spaceName {
    color: royalblue;
}
.chat > footer {
    border: 1px solid royalblue;
}
.spaceMedia {
    background: black;
    color: royalblue;
    border-left: 1px solid royalblue;
}
.spaceMedia > .empty .card {
    background: black;
    color: royalblue;
    border: 1px solid royalblue;
}
.spaceMedia > .empty .card p {
    color: royalblue;
}
.spaceMedia .exploreButton {
    background: royalblue;
}
.inviteButtonArea .inviteButton {
    background: black;
    color: royalblue;
    border: 1px solid royalblue;
}
.inviteButtonArea .inviteButton:hover {
    background: black;
    color: royalblue;
    border: 1px solid royalblue;
}
.inviteButtonArea .inviteButton span {
    color: royalblue;
}
.toolbar .toolbar .navBar {
    background: black;
    color: royalblue;
}
.toolbar .toolbar.default {
    background: black;
    color: royalblue;
}
.toolbar .toolbar.default .controls .menu {
    background: black;
    border: 1px solid royalblue;
}
.toolbar .toolbar.default .controls .menu .item {
    color: royalblue;
    border-top: 1px solid royalblue;
}
.chat > footer > .buttonOverlay > .send {
    background: royalblue;
}
.spacesView .spaces:not(.room) .tray > .spacesHeader > .spacesTitle {
    color: royalblue;
}
.friendsPopup > .popup {
    background: black;
    color: royalblue;
    border: 1px solid royalblue;
}
.spaceHeader > .left > .spaceName {
    color: royalblue;
}
.accountSettingsPopup > .popup {
    background: black;
    color: royalblue;
    border: 1px solid royalblue;
}
.accountSettingsPopup > .popup .infoForm .leftSide .inputBlock .inputLabel,
.accountSettingsPopup > .popup .infoForm .rightSide .inputBlock .inputLabel {
    color: royalblue;
}

.accountSettingsPopup > .popup .infoForm .leftSide .inputBlock .inputContainer > input[type="text"],
.accountSettingsPopup > .popup .infoForm .leftSide .inputBlock .inputContainer input[type="password"],
.accountSettingsPopup > .popup .infoForm .rightSide .inputBlock .inputContainer > input[type="text"],
.accountSettingsPopup > .popup .infoForm .rightSide .inputBlock .inputContainer input[type="password"] {
    background: black;
    color: royalblue;
    border: 1px solid royalblue;
}
.accountSettingsPopup > .popup .headerSettings .title {
    color: royalblue;
}
}

@-moz-document url-prefix("https://www.rabb.it/") {
.toolbar .toolbar.default {
    background: black;
    color: royalblue;
}
.toolbar .toolbar.default .controls .menu {
    background: black;
    color: royalblue;
    border: 1px solid royalblue;
}
.toolbar .toolbar.default .controls .menu .item {
    color: royalblue;
    border-top: 1px solid royalblue;
}
.compositeView .baseSliderView .title {
    color: royalblue;
}
.compositeView .homeInterestRoomView .contentInfo .sessionsInfo .hostUserName {
    color: royalblue;
}
.compositeView .homeInterestRoomView .contentInfo .sessionsInfo .numSessions {
    color: royalblue;
}
.compositeView .homeInterestRoomView .resizeWrapper .content .hopIn {
    background: royalblue;
    border: 1px solid royalblue;
}
.compositeView .homeInterestRoomView .resizeWrapper .content .hopIn.active,
.compositeView .homeInterestRoomView .resizeWrapper .content .hopIn:hover {
    background: royalblue;
    border: 1px solid royalblue;
}
#rabbitapp[role="document"] .main {
    background: black;
    color: royalblue;
}
}

@-moz-document url-prefix("https://www.rabb.it") {
.chat .body .message.me .messageBody {
    background: black;
    color: royalblue;
    border: 1px solid royalblue;
}
.chat .body .chatFadeOverlay .typingActivity {
    color: royalblue;
}
.desktop .roomLayout .mainRoom .videoChatView .rightSide .chatLayout .messaging .chatReactBridge .chat .message.other .messageBody {
    color: royalblue;
    border: 1px solid royalblue;
}
.chat .body .message header .userName {
    color: royalblue;
}
.chat .body .list {
    background: black;
}
.desktop .roomLayout .mainRoom .videoChatView .leftSide .flexer > .tray .trayView .topLine:not(.noBubbles) {
    background: black;
}
.desktop .roomLayout .mainRoom .videoChatView .leftSide .flexer > .tray .trayView {
    background: black;
}
.desktop .roomLayout .mainRoom .videoChatView:not(.spaceRoom) > .leftSide > .flexer > .controls .roomControlsLayoutView {
    background: black;
}
.desktop .roomLayout .mainRoom .videoChatView .rightSide .chatLayout .messaging,
.desktop .roomLayout .mainRoom .videoChatView .rightSide .chatLayout .messaging .chatReactBridge {
    background: black;
}
.toolbar .toolbar.room {
    background: black;
    color: royalblue;
}
#rabbitapp[role="application"] .banner,
#rabbitapp[role="application"] .header {
    background: black;
}
.desktop .roomLayout .mainRoom .videoChatView .rightSide .chatLayout .header .roomChatHeader .contentDetails .primary {
    color: royalblue;
}
.inviteButtonArea.publicRoom .inviteButton,
.inviteButtonArea.room .inviteButton {
    background: black;
    color: royalblue;
    border: 1px solid royalblue;
}
.inviteButtonArea.publicRoom .inviteButton span,
.inviteButtonArea.room .inviteButton span {
    color: royalblue;
}
.desktop .roomLayout .mainRoom .videoChatView .rightSide .chatLayout .header .roomChatHeader .actions .watching {
    color: royalblue;
    border: 1px solid royalblue;
}
.desktop .roomLayout .mainRoom .videoChatView .rightSide .chatLayout .messaging .chatReactBridge .chat footer textarea {
    color: royalblue;
    border: 1px solid royalblue;
}
.chat > footer > .buttonOverlay > .send {
    background: royalblue;
}
}
