css:
@-moz-document domain("octicons.github.com") {
:root {
  --color-bg: #333;
  --color-fg: #f5f5f5;
  --color-bg-dark: #222;
  --color-bg-light: #444;
  --color-fg-dark: #ccc;
  --color-link: #508ccf;
}
body {
  background-color: #333;
  color: var(--color-fg);
}
a {
  color: var(--color-link);
}
header, .footer {
  background-color: var(--color-bg-dark);
}
header .logo {
  color: var(--color-fg-dark);
}
header .logo:hover {
  color: var(--color-link);
}
.header-actions a {
  color: var(--color-fg-dark);
}
.header-actions a:hover {
  color: var(--color-link);
}
.lead {
  color: var(--color-fg-dark);
}
hr {
  background-color: var(--color-bg-light);
}
.icon-list li {
  background-color: transparent;
}
.icon-list a {
  background-color: var(--color-bg-light);
  border-color: var(--color-bg-light);
  color: var(--color-fg);
}
.octicon {
  fill: var(--color-fg);
}
.icon-preview {
  background-color: #fff;
}
.icon-preview .octicon {
  fill: #000;
}
.shadow-wrap.light .preview-background {
  background-color: #000;
}
.light .icon-preview .octicon {
  fill: #fff;
}
.markdown-body .highlight pre,
.markdown-body pre {
  background-color: var(--color-bg-light);
}

}
