css:
@-moz-document url-prefix("https://www.wunderground.com/personal-weather-station/dashboard") {
.favs-wrapper {
    display: none;
}
.topbar {
    display: none;
}
#pws-location {
    display: none;
}
#visit-count {
    display: none;
}
#online {
    content: 'whatever it is you want to add';
}
#curTemp {
    font-size: 100px;
}
#current-conditions, #current-conditions * {
    visibility: visible;
}
body * {
    visibility: hidden;
}
a.wundermap_link.show-for-medium-up {
    display: none !important;
}

}
