css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://www.tumblr.com/dashboard"), url-prefix('https://www.tumblr.com/dashboard')  {
* {-moz-border-radius: 0px !important;}
img#content_tab_left {display: none !important;}
#content_tab {padding-left:40px !important;padding-right:40px !important;}

#container{width:1830px !important;
margin:0 !important;
padding:0 10px !important
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
padding: 10px !important;
}

#left_column{
float:left !important;
width:1570px !important;
-moz-column-count: 3 !important;
-moz-column-gap: 25px !important;
}

.arrow {display:none !important;}

.new_post_notice img {display:none !important;}

li.post.new_post {
display:none !important;
}
ol#posts li.post {
border-bottom: none !important;
margin: 0 0 10px 0px !important;
width: 500px;
padding:15px 10px !important;
}
ol#posts {margin: 0 !important;}

.avatar {
height:30px !important;
left:5px !important;
overflow:hidden;
position:absolute;
text-decoration:none;
top:0px;
width:30px !important;
border-bottom: none !important;
margin: 8px 0 0 5px;
}

.post_info {padding: 0 0 0 40px;}

}
