css:
@-moz-document url-prefix("https://team_ygg.yggtorrent.is") {

input.rc-input__element.rc-input__element--small, input#message-search
{
	background-color: inherit;
	border:1px black solid;
}

span.role-tag.background-info-font-color
{
	background-color: inherit;
}

.sidebar-item__name
{
	color:#9ea2a8;
}

.rooms-list, footer.sidebar__footer, header.sidebar__header, ul, header.fixed-title.content-background-color.border-component-color, .flex-tab-bar.content-background-color, .rc-message-box.rc-newdiv.rc-message-box, .emojis, .emoji-top, .flex-tab-bar.content-background-color, .wrapper, .rooms-list, footer.sidebar__footer, .toolbar, .sidebar__account.active, header.sidebar__header, header.fixed-title.content-background-color.border-component-color, .rc-popover__content, .content.flex-tab__content, span.secondary-background-color, .list-view.channel-settings, .tab-button.active, .list-view.flex-tab__header.search-messages-list, .flex-tab__result.list-view, .title,.list-view.flex-tab__header, .flex-tab .message.new-day::before,.list-view.push-notifications.rc-message-box.rc-new, footer.footer.border-component-color, .list-view.push-notifications, .content, .preferences-page__content, section.preferences-page, header.fixed-title.border-component-color, .message.new-day::before
{
	background-color: white;
}

span.badge
{
	background-color: inherit;
	border:1px black solid;
}

button.user.user-card-message.color-primary-font-color
{
	color:#ff7000;
}

svg
{
	color:#00a3ff!important;
}

a.mention-link
{
	color:#00a3ff!important;
}

.message a
{
	color:#00a3ff!important;
}

i
{
	color:#00a3ff!important;
}

.badge
{
	color:#00a3ff!important;
	border:1px white solid;
}

i
{
	color:#00a3ff!important;
}

.body.color-primary-font-color, ul.list.clearfix, .flex-tab-container.border-component-color.opened, .content
{
	color:black;
}

h2, span.rc-popover__item-text, .sidebar__account-visual-status, .preferences-page__content, section.preferences-page
{
	color:black;
}

h3.rc-popover__title
{
	color:white;
}

p, .message.new-day::before, span.time
{
	color:black;
}

textarea.rc-message-box__textarea.js-input-message.autogrow-short
{
	color:black;
}

span.info.border-component-color.color-info-font-color
{
	font-size: 10px!important;
}

.rc-popover__content
{
	border:1px #00a3ff solid;
	border-radius:5px;
}

.border-component-color
{
	border-color: #00a3ff;
}

.rc-message-box__container
{
	border-color: #00a3ff;
	border-radius:15px;
}

/*COULEUR DE LA FONCTION "ADMIN"*/
span.role-tag.background-info-font-color[data-role="Admin"]{
    color:red;/*COULEUR ROUGE*/
    border:none;/*PAS DE CADRE*/
}



/*COULEUR DE LA FONCTION "MODÉRATEUR"*/
span.role-tag.background-info-font-color[data-role="Super-Modérateur"],span.role-tag.background-info-font-color[data-role="Modérateur Forum"]{
    color:green;/*COULEOR VERTE*/
    border:none;/*PAS DE CADRE*/}



/*COULEUR DE LA FONCTION "modo-shout"*/
span.role-tag.background-info-font-color[data-role="Modérateur Shoutbox"]{
    color:#00a3ff;/*COULEUR BLEU*/
    border:none;/*PAS DE CADRE*/}



/*COULEUR DE LA FONCTION "Team-Pending"*/
span.role-tag.background-info-font-color[data-role="Team-Pending"], span.role-tag.background-info-font-color[data-role="Responsable Team Pending"]{
    color:orange;/*COULEUR ORANGE*/
    border:none;/*PAS DE CADRE*/}



/*COULEUR DE LA FONCTION "Team-Vérole"*/
span.role-tag.background-info-font-color[data-role="Vérole"], span.role-tag.background-info-font-color[data-role="Responsable Team Vérole"]{
    color:orange;/*COULEUR ORANGE*/
    border:none;/*PAS DE CADRE*/}



/*COULEUR DE LA FONCTION "Correcteur"*/
span.role-tag.background-info-font-color[data-role="Correcteur"]{
    color:#ff00e1;/*COULEUR ROSE*/
    border:none;/*PAS DE CADRE*/}

/*COULEUR DE LA FONCTION "Comique de service"*/
span.role-tag.background-info-font-color[data-role="Comique de service"]{
	color:yellow;/*COULEUR ROSE*/
    border:none;/*PAS DE CADRE*/}

/*SUPPRESION DU STATUT " STAFF"*/
span.role-tag.background-info-font-color[data-role="Staff"]{
    display:none;}


button[data-link="https://github.com/Khan/KaTeX/wiki/Function-Support-in-KaTeX"]
{
	display:none;
}

.inline-image
{
	border:1px black solid;
}

li.rc-popover__item[data-id="online"] svg.rc-icon.rc-popover__icon-element.rc-popover__icon-element--circle
{
	color:green!important;
}

li.rc-popover__item[data-id="away"] svg.rc-icon.rc-popover__icon-element.rc-popover__icon-element--circle
{
	color:yellow!important;
}

li.rc-popover__item[data-id="busy"] svg.rc-icon.rc-popover__icon-element.rc-popover__icon-element--circle
{
	color:red!important;
}

li.rc-popover__item[data-id="offline"] svg.rc-icon.rc-popover__icon-element.rc-popover__icon-element--circle
{
	color:gray!important;
}

}
