css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("mastodon.social"), domain("awoo.space"), domain("witches.town"), domain("icosahedron.website"), domain("chitter.xyz"), domain("monsterpit.net"), domain("cybre.space"), domain("glitch.social"), domain("occult.camp"), domain("social.tchncs.de"){
.account__avatar{
	border-radius: 100%;
}

:not(.notification) .account__avatar-overlay .account__avatar-overlay-overlay{
	display:none;
}
:not(.notification) .account__avatar-overlay .account__avatar-overlay-base{
	width: 48px;
    height: 48px;
    background-size: 48px 48px;
    border-radius: 100%;
}
.notification__message .fa{
	margin-left: -30px;
    margin-top: 8px;
}
.notification .account__avatar-overlay .account__avatar-overlay-overlay{
	display: initial;
    /*width: 48px;
    height: 48px;
    background-size: 48px;*/
	width: 24px;
    height: 24px;
    background-size: 24px 24px;
    border-radius: 100%;
    bottom: inherit;
    right: 0;
    top: -24px;
}
.notification .account__avatar-overlay .account__avatar-overlay-base{
	display:none;
}
}
