css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("macsurfer.com") {
  td:first-child[width="150"],
  table.text_top_box[width="90%"],
  body > table:first-child > tbody > tr:first-child,
  body > table:first-child > tbody > tr:first-child + tr + tr,
  body > table:first-child > tbody > tr > td > table:first-child > tbody > tr:first-child,
  table table table > tbody > tr:first-child > td:first-child > center:first-child,
  td:first-child[width="150"][valign="top"][align=left] + td + td[valign=top][align=right],
  iframe {
    display: none !important;
  }
  td.main_headlines {
    background-color: #296B94 !important;
    color: white !important;
  }
}
