css:
@-moz-document regexp("https://anistar.me/.*") {
/***... Main page ...***/
body { /* Фон сайта и цвет текста */
    background: #252525!important;
    color: #e8e8e8!important;
}

/*** Реклама ***/
/*
center {
	display: none;
}
#animation_container {
	display: none;
}
*/
.video2 { /* Видео реклама */
	display: none;
}
.newclasswqasdvsdv { /* Видео реклама */
    display: none;
}
.a-message { /*Сообщение о блокировке рекламы*/
	display: none!important;
}


/*** Баги цвета***/
.tags a { /* Цвет ссылок видео */
    color: #fff!important;
}
.title-2 { /* Цвет текста меню */
	color: #000!important;
}
.col-text { /* Цвет ссылок меню */
	color: #000!important;
}
.block-text { /* Цвет ссылок правого блока */
   color: #000 !important;
}
.mess { /* Цвет сообщений чата */
	color: #000!important;
}
.cat-cal { /* Цвет текста ANIME */
	color: #000!important;
}

/*** Меню ***/
header .menu.active > p, header .menu:hover > p { /* Цвет фона кнопки меню */
	background: #CCC!important;
}
.drowmenu { /* Цвет фона меню */
	background: #CCC!important;
}
.ui-widget-content { /* Текст Поиск */
    background: #CCC!important;
}
body header { /* Баг отступа */
    padding-bottom: 0px!important;
}
.list-nav { /* Отступ меню */
    margin-top: 5px; 
}
.mini_nav { /* Размер правого текста меню */
	width: 290px;
}

.panel.active.profile-top.in_login .hidden-panel {
	width: 347px;
}

/*** Левый блок ***/
.scoro { /* Скоро */
    float: left;
    width: 899px;
    margin-left: 0;
}
.main-cal { /* Скоро блоки*/
    float: left;
    width: 48%;
    margin: 1%;
}
.short .news { 
	margin-top: 0px;
    margin-bottom: 5px
}
.text-s { /* Текст Аниме */
	display: none;
}
.donat { /* Донат */
	display: none;
}
.topusers { /* Топ донатеры */
	display: none;
}
.appfown { /* Текст Аниме */
	display: none;
}

/*** Правый блок ***/
.content-right {
	margin-top: 5px;
}
.title-3 { /* Текст Поиск */
	display: none;
}
.search-block .sear_but { /* Input размер блока поиска */
	height: 27px; 
}
.tit_sp:hover { /* Правое меню цвет жёлтый*/
	background: #FED000!important;
}
.hid_ul.act_hid .tit_sp { /* Правое меню цвет жёлтый*/
	background: #FED000!important;
}
.hid_ul.act_hid ul a:hover { /* Правое меню цвет синий*/
	background: #095CA8!important;
}
.hid_ul.act_hid ul a { /* Правое меню отключаем фон*/
	background: none!important;
}
.new_menu {
    background: none!important;
}
.soc { /* Баг */
	display: none;
}

.qiwi { /* Отступ блока донатов */
    padding-bottom: 5px;
}

/*** Тррент блок ***/
.torrent .cont { /* Отключаем текстуру фона */
	background: none!important;
}
.torrent .info_d { /* Цвет фона "Скачать торрент" */
	background: #FEFEFE!important;
}

/*** Раздел Аниме ***/
#news_set_sort a.sort-actv {  /* Цвет текста фильтров" */
	color: #FFF!important;
}

.button_bt:hover { /* Цвет текста ВСЕ ЖАНРЫ" */
	color: #FFF!important;
}
}
