css:
@-moz-document domain("www.hdkinoteatr.com") {
#intro-img, .more-link, div.off, div.inset div div, div.intro, div.dpad center, div.leftcol, div.rightcol, .footer, div.contentTop, div.contentBtm {display: none;}
h2.btl a {font-size: 3em !important;}
div.descr {
    font-size: 35px;
    color: #ffffff;
    font-weight: normal;
    line-height: 120%;height: 100% !important;
}
h1.btl, h2.btl a {color: #31ff0b;
    font-size: 46px;
    border-style: ridge;
    border-color: #4a2afc;
    border-width: 5px;
    background: -webkit-linear-gradient(top, #0A2C67 0%, #000 100%);
}
div.content {position: absolute; left: 20px !important; top: 100px;
    width: 97%;
}
p.argcat {
    font-size: 30px;
    color: #fafafa;
}
p.argcat a {
    color: #00ff33;
}
span.thide.pnext {
    font-size: 250%;
    border-style: solid;
    border-color: #05ff54;
    color: #08ff10;
}
span.thide.pprev {
    border-color: #ff123a;
    font-size: 250%;
    border-style: solid;
    color: #ff1;
}
a.thide.toptop {
    font-size: 250%;
    color: #ff0000;
    border-style: solid;
    border-color: #ff0d0d;
}

#vid {
    position: absolute; top: 0px !important;left: 0px !important;min-width: 100%;min-height: 510px;
    box-shadow: 0px 0px 6px 11px #7d0606;
    background: -webkit-linear-gradient(top, #0A2C67 0%, #000 100%);
}
}

@-moz-document url("http://www.hdkinoteatr.com/top/") {
#dle-content {
    font-size: 300%;
    color: #ffffff;
}
a.lnkTip {
    color: #08ff10 !important;
}
li h1 a {
    font-size: 250% !important;
}
div.dpad.top {
    padding-top: 20px;
    margin-left: 80px;
}
}
