css:
@-moz-document domain("nexusmods.com") {
@media (max-width: 1280px) and (min-width: 767px) {
	nav#menu ul.wrap li ,
	form.search .searchfield {
		display    : inline-block;
		flex-grow  : initial;
	}
	#nav-search {
		display    : inline-block;
		flex       : initial;
		margin-top : 4px;
	}
	#nav-search .btn[type="submit"] {
		display : none;
	}
	.nav-game-name {
		max-width : 180px;
	}
	#gsearch {
		width : 180px;
	}
	nav#menu .wrap ul li a {
		padding-left  : 15px;
		padding-right : 15px;
	}
}
@media (max-width: 980px) and (min-width: 767px) {
	nav#menu .wrap ul li a {
		padding-left  : 13px;
		padding-right : 13px;
	}
	#gsearch {
		width : 200px;
	}
}
@media (max-width: 940px) and (min-width: 767px) {
	nav#menu .wrap ul li a {
		padding-left  : 11px;
		padding-right : 11px;
	}
	.nav-game-name {
		max-width : 170px;
	}
}
@media (max-width: 910px) and (min-width: 767px) {
	nav#menu .wrap ul li a {
		padding-left  : 9px;
		padding-right : 9px;
	}
	.nav-game-name {
		max-width : 160px;
	}
}
@media (max-width: 890px) and (min-width: 767px) {
	nav#menu .wrap ul li a {
		padding-left  : 7px;
		padding-right : 7px;
	}
	.nav-game-name {
		max-width : 150px;
	}
}
@media (max-width: 850px) and (min-width: 767px) {
	nav#menu .wrap ul li a {
		padding-left  : 5px;
		padding-right : 5px;
	}
	.nav-game-name {
		max-width : 140px;
	}
}
@media (max-width: 810px) and (min-width: 767px) {
	nav#menu .wrap ul li a {
		padding-left  : 3px;
		padding-right : 3px;
	}
	.nav-game-name {
		max-width : 124px;
	}
}
}
