css:
@-moz-document domain('wikipedia.org'),
    domain('wikimedia.org'),
	domain('wikibooks.org'),
	domain('wikiversity.org'),
	domain('wikisource.org'),
	domain('wikinews.org'),
	domain('wikiquote.org'),
	domain('wiktionary.org'),
	domain('wikidata.org'),
	domain('mediawiki.org'){
    
/*lighter background*/

.mw-body, .parsoid-body {
    background-image: url(https://i.imgur.com/qZwNeBf.png);
}

div.vectorTabs li.selected {
    background-image: url(https://i.imgur.com/qZwNeBf.png) !important; /*active tab*/
}

/*darker background*/

body {
    background-image: url(https://i.imgur.com/P8gPPFF.png); /*left menu*/
}

div.vectorTabs ul li {
    background-image: url(https://i.imgur.com/P8gPPFF.png) !important; /*inactive tab*/
}

#mw-head {
    background-image: url(https://i.imgur.com/P8gPPFF.png);
}

#mw-page-base {
    background-image: url(https://i.imgur.com/P8gPPFF.png);
}

.app-badges {
    background-image: url(https://i.imgur.com/P8gPPFF.png);
}
    
}
