css:
@-moz-document domain("linux.org.ru") {
html,
body
{
  background: #024444 !important;
  color: #fff04c !important;
}

.code,
code
{
  background: #033;
  color: #99ffff;
}

blockquote
{
  font-style: normal;
  color: #99ffff;
  background: #033;
  border-left-width: 0px;
  border-right: 0px solid;
  border-bottom: 1px solid;
  border-color: #dcdcdccc;
}

#bd a.tag
{
  color: #d4d4d4;
}

.boxlet
{
  background-color: #dcdcdc36 !important;
  border: 1px solid #969696;
  border-radius: 0;
}

a
{
  font-weight: bold;
  color: #6552ffcc !important;
}

a.visited
{
  color: #000000 !important;
}

.infoblock
{
  background: #dcdcdc45 !important;
  border-radius: 0;
}

.messages .msg
{
  background: none repeat scroll 0% 0% #023333;
  border-top: 1px solid;
  border-bottom: 1px solid;
  border-right-width: 0;
  border-left-width: 0;
  border-radius: 0;
  border-color: #1c1d1dcc;
}

.messages .msg:target
{
  border: 1px solid #101111;
}

.nav-buttons a
{
  color: #c2640e !important;
}

#bd .btn
{
  border: 1px solid #969696;
  color: #545454;
}

.page-number
{
  border: 2px solid;
  border-radius: 1em;
  border-color: #3d5fcccc;
  background-color: #060b4ccc;
}

#bd .forum table th
{
  background: #8c8c8c !important;
  border: 1px solid #b1b1b1;
}

.message-table .tag
{
  background: #340404b3;
}

.message-table tr:hover
{
  background: #d6d6d659 !important;
}

.message-table a.secondary
{
  color: #ba7608 !important;
}

/* Логотип `LINUX.ORG.RU` */
#sitetitle
{
  color: #e4780b !important;
}

h2 a
{
  color: #dee61f !important;
}

#bd h1,
#bd h2
{
  color: #c4660f;
}

.menu a
{
    color: #cb9a11 !important;
    text-decoration: none;
}

#loginGreating a
{
  color: #1d9ff1 !important;
  text-decoration: none;
}

#loginGreating a:before,
.head a[href*="people"]:before
{
  content: "@";
  font-size: 1em;
}

/* Кнопки фильтрации по категориям */
#bd .btn-selected
{
  background-color: #cd8105;
  color: #c1c1c1;
}

#bd .btn-default
{
  background-color: #d6d6d6;
}

#bd .forum table tbody td
{
  border: 0px solid #b1b1b1;
}

/* Левая колонка таблицы в `Уведомлениях` */
#bd .icon-reply-color,
#bd .icon-tag-color
{
  color: #ebe2e2;
}

#whois_userpic
{
    background: none repeat scroll 0% 0% #e0e0e0;
}

#bd .btn-primary
{
    background-color: #1fafaf;
    color: #dcdcdc;
}

#bd .btn-danger
{
    background-color: #1fafaf;
    color: #ffffff;
}

img
{
    filter: contrast(100%) brightness(90%);
}

.stars
{
  color: #999900;
}

.group-label
{
  background-color: #222222;
}

.fav-buttons .selected [class^="icon-"]
{
  color: #1fafaf;
}

/* Комментарий к юзеру */
.user-remark
{
  color: #3a3a3a;
}

#bd .forum table thead,
#bd .forum table tfoot
{
  color: #ededed;
}

span.group-label
{
    color: #ffffff;
}
}
