css:
@font-face {
  font-family: 'Ubuntu';
  font-style: normal;
  font-weight: normal;
  src: local('Ubuntu Regular'), local('Ubuntu-Regular'), url(https://fonts.gstatic.com/s/ubuntu/v11/4iCs6KVjbNBYlgoKfw72.woff2) format('woff2');
}
@font-face {
  font-family: 'Ubuntu Mono';
  font-style: normal;
  font-weight: normal;
  src: local('Ubuntu Mono'), local('UbuntuMono-Regular'), url(https://fonts.gstatic.com/s/ubuntumono/v7/ViZhet7Ak-LRXZMXzuAfkY4P5ICox8Kq3LLUNMylGO4.woff2) format('woff2');
}
/* i really want this to be global */
* {
    font-family: Ubuntu;
}
body, body * {
    font-family: Ubuntu;
}
.pl-c,
pre,
pre * {
    font-family: Ubuntu Mono !important;
    font-style: normal !important;
}

a, a *,
blockquote, blockquote *,
h1, h1 *,
h2, h2 *,
h3, h3 *,
h4, h4 *,
h5, h5 *,
p, p * {
    font-family: Ubuntu;
}

@-moz-document domain("facebook.com"), domain("gizmodo.com"), domain("arstechnica.com") {
a,
body,
button,
div,
div *,
input,
label,
p,
select,
td,
textarea,
#facebook ._-kb, 
.userContent {
    font-family: Ubuntu !important;
}
}
