css:
@-moz-document domain("0815.at"), domain("0815.eu") {
body {
    background: #000;
}

header {
    background: #000;
}

.ribbon-header.nav-is-opened:after, .ribbon-header.nav-is-opened:before, .ui-desktop .ribbon-header.nav-is-opened, .withMegaDropdown .nav-main, .withMegaDropdown .nav-main .nav-list, .sticky-mode .nav-main.state-manageable .additionalMenuSlider-manager, .ui-desktop .bottom-header {
    background: #000;
}

.nav-list-sub, .nav-wrapper, .nav-item-root.hover:not(.inline-styles) .hyp-top-lvl, .nav-item.hover:not(.inline-styles) .hyp-top-lvl {
    background-color: #000;
}

.simple-nav-list, .nav-wrapper, .nav-item.hover:not(.inline-styles) .hyp-top-lvl {
    background: #000;
}

.nav-item.mega-dropdown .nav-wrapper .nav-lvl-3 {
    background: #000;
}

header.sticky-mode, header.sticky-mode .bottom-header, .ui-mobile header {
    background: #000;
}

.sticky-mode .over-summary {
    background: #232323 !important;
}

.tbx-main-search {
    background: #000;
}

.action-bg-wrp {
    background-color: #232323;
}

footer {
    background: #000;
}

.colored-header-desktop {
    background-color: #232323;
}

.facet-slider .ui-widget-content, .filters-wrapper {
    background: #232323;
}

.ui-tabs-active .ui-tabs-anchor:before, .ui-tabs-active .ui-tabs-anchor:after {
    background: #1d1d1d;
}

.ui-tabs-tab .ui-tabs-anchor:after {
    background: #1d1d1d;
}
}
