css:
@-moz-document domain("youtube.com")
{ 
  
#items.ytd-watch-next-secondary-results-renderer {
  display: none;
}

paper-button.style-scope.yt-next-continuation {
  display: none;
}

.html5-endscreen {
  display: none;
}
  
#main.ytd-watch, #alerts.ytd-watch, #chat.ytd-watch, #comments.ytd-watch, #pla-shelf.ytd-watch, #info.ytd-watch, #meta.ytd-watch, #ticket-shelf.ytd-watch, #merch-shelf.ytd-watch, #report.ytd-watch, #player.ytd-watch, #playlist.ytd-watch, #related.ytd-watch, #transcript.ytd-watch {
  box-sizing: border-box;
  margin: 0 auto;
}
  
ytd-masthead[mode="legacy"] ytd-searchbox.ytd-masthead, ytd-masthead[mode="material-left"] ytd-searchbox.ytd-masthead {
  justify-content: center;
}

}
