css:
@-moz-document domain('guide.meteor.com'), domain('docs.meteor.com') {
div.nav {
    position: relative !important;
}
body,
div {
    color: #08bb88 !important;
}
body,
.nav,
.content,
.sidebar,
.header-content {
    background-color: #171819 !important;
    background-image: none !important;
}
code,
pre,
.note,
.api {
    color: #b58900 !important;
    background: #041515 !important;
}
.btn.tertiary {
    color: #d68f0a !important;
}
.api {
    border: 1px solid #673a3a !important;
}
.nav,
.api-heading,
.btn.tertiary {
    background: #080808 !important;
}
.sidebar a,
blockquote {
    color: #f18989 !important;
}
.sidebar .heading-toc,
.nav a {
    color: #f9f9f9 !important;
}
.document-formatting h2,
.document-formatting h3 {
    color: #a9a9a9 !important;
}
.wrapper-search input {
    background-color: #1e1e1eeb !important;
    color: white !important;
}
body > div.content {
    margin-left: 20px !important;
}
}
