css:
@-moz-document url-prefix("https://office.bexio.com") {
    *,
    .ui-widget.ui-widget-content.ui-datepicker .ui-datepicker-buttonpane button.ui-datepicker-current.ui-priority-primary,
    .ui-widget.ui-widget-header.ui-datepicker .ui-datepicker-buttonpane button.ui-datepicker-current.ui-priority-primary,
    .ui-widget.ui-widget-content.ui-datepicker .ui-datepicker-buttonpane button.ui-datepicker-close.ui-priority-primary,
    .ui-widget.ui-widget-header.ui-datepicker .ui-datepicker-buttonpane button.ui-datepicker-close.ui-priority-primary,
    .ui-widget.ui-widget-content.ui-datepicker .ui-datepicker-buttonpane button.ui-datepicker-current.ui-priority-secondary,
    .ui-widget.ui-widget-header.ui-datepicker .ui-datepicker-buttonpane button.ui-datepicker-current.ui-priority-secondary,
    .ui-widget.ui-widget-content.ui-datepicker .ui-datepicker-buttonpane button.ui-datepicker-close.ui-priority-secondary,
    .ui-widget.ui-widget-header.ui-datepicker .ui-datepicker-buttonpane button.ui-datepicker-close.ui-priority-secondary {
        color: white!important;
    }
    body {
        background-color: black;
    }
    .main_block,
    .foot,
    .head,
    .bx-formular-container,
    .bx-formular-container-head{
        background-color: #0b0b0b !important;
    }
    input,
    .input,
    #tinymce,
    textarea,
    select,
    .time-picker,
    .ui-widget.ui-widget-content.ui-datepicker,
    .ui-widget.ui-widget-header.ui-datepicker,
    .ui-widget-content,
    .select2-drop,
    .select2-results .select2-no-results,
    .select2-results .select2-searching,
    .select2-results .select2-selection-limit,
    .select2-choice,
    .ac_results,
    .autocompleterQuickAdd,
    .dropdown-menu,
    .bx-dashboard-card,
    .gridster .gs-w,
    .ac_results li.autocompleterNoResults{
        background: none !important;
        background-color: #222222!important;
    }
    .mceToolbar,
    .nav-tabs > .active > a,
    .btn,
    .add-on,
    .ac_results ul li.ac_over,
    .mypager .pagination a,
    .nav > li > a:hover,
    #char_selector ul li a,
    .select2-results .select2-highlighted
    {
        background: none !important;
        background-color: #555555 !important;
    }
    .es_footer,
    #ubs-blog-info,
    .recommend-dropdown,
    .logo_holder,
    .nav_payroll,
    .usersnap{
        display: none !important;
    }
    .tab-content,
    .bx-formular-header,
    td,
    .foot,
    .dataTableHolder,
    .head {
        border: 0px solid black !important;
    }
}

@-moz-document url-prefix("https://office.bexio.com/index.php/monitoring/edit") {
    .bx-card-title .btn {
        display: none;
    }

}
