css:
@-moz-document domain("reddit-stream.com") {
  body {
    background-color: black;
    color: white;
  }
  div.c-body {
    background-color: black;
  }
  div.c-right-info {
    color: black;
  }
  div#c-main {
    width: 95%;
  }
  div#sidebar {
    display: none;
  }
  a.c-username {
    color: lawngreen!important;
  }
  span.c-flair {
    color: darksalmon!important;
    border: none;
    font-style: italic;
  }
  div.c-body {
    border: none;
  }
  a.c-points {
    color: darkturquoise!important;
  }

}
