@-moz-document url(https://web.whatsapp.com/) {

/* --- Kill unused styles --- */
.app-wrapper::after {display:none;}

/* --- General --- */
.app-wrapper-web .app {box-shadow: none;border-radius: 0;height: 100%;top: 0;width: 100%;}
.app-wrapper-web .app header {background-color: #128C7E;}
.app-wrapper-web .app header div {color:#fff;}
.app-wrapper-web .app header span {color:#fff;}
.app-wrapper-web .app header svg path {fill:#fff;}
.app-wrapper-web .app img {border-radius: 0;}

/* --- Panel Left --- */
#pane-side
.avatar-image {border-radius: 0;}
._2EXPL .avatar {transition: .2s;}
._1f1zm {background: rgba(4,94,84,.1);}
.chat.active .avatar {width: 60px!important;height: 60px!important;}

/* --- Header Right --- */
#main header div:nth-child(2) div:nth-child(2) span {color:rgba(255,255,255,.6);}

/* --- Panel Right --- */
#main {background: url(http://www.mulierchile.com/white-background-wallpaper/white-background-wallpaper-007.jpg);background-repeat: no-repeat;background-position: center;background-size: cover;}
.pane-chat-tile {background: none;}

/* --- Panel Right: Input --- */
html[dir] ._3XpR2 {padding: 20px; background: none;}
html[dir] ._3XpR2 .block-compose {border-radius: 7.5px;background: #fff;}

}
