css:
@-moz-document url-prefix("https://segmentfault.com/") {
/* header */
.sf-header .container.nav {
	width: 100%;
}
@media (min-width: 1200px) {
	.header-search {
		width: 500px;
	}
}

/* body */
.wrapper .container, .post-topheader .container, .wrap > .container, .container.live-index {
	width: 100%;
}
.post-topheader .container, .wrap > .container {
	padding: 0 40px;
}

.live-index-banner {
	display: none;
}
.wrapper .container > .row > .middle > div:nth-child(1),  
.wrapper .container > .row > .middle > div:nth-child(1) * {
	display: none;
}

/* body-nav-left */
.left .tech-square-item {
	padding-left: 25px
}

/* body-nav-right */
@media (min-width: 1440px) {
	.right .short-cut-area .item {
		width: 25%;
		border: none !important;
	}
}

/* others */
.license-tooltip {
	display: none;
}
}
