css:
@-moz-document url-prefix("https://mail.google.com") {
/* 	Take border off tabs */
.aAy,
.J-KU-KO .aAy {
    border: none;
}

/* Take white away under tabs */
.aKm,
.J-KU-Jg-K9.aAA .aKi {
    background: none;
    border: none;
    border-width: 0;
    border-style: none;
}

/* Takes away transparent mail preview */
.yO,
.J-KU-Jg-K9.aAA .J-KU-KO.aAy {
    background: rgba(0, 72, 81, 1) !important;
    color: #fff;
}

/* 
	Take away transparent dropdown 
	in new mail message window 
*/
.Y1 {
	opacity: 1;
}

/* Make stuff green */
.T-I-KE,
.T-I-atl,
.TI .T-I-ax7,
.z0 .T-I-ax7,
.G-atb .T-I-ax7,
.T-I-ax7,
.zA,
#gbqfb {
    background: none;
    background-color: #004851;
    background-image: none;
    box-shadow: none;
    color: #fff;
    opacity: 1;
    text-shadow: none;
}

/* Make stuff green */
.T-I-atl:active,
.T-I-KE:active,
.TI .T-I-ax7:active,
.z0 .T-I-ax7:active,
.G-atb .T-I-ax7:active,
.T-I-ax7:active,
#gbqfb:active {
    background: none;
    background-color: #004851;
    background-image: none;
    border: 1px solid #333;
    box-shadow: none;
    color: #fff;
}

/* Make stuff green */
.T-I-KE:hover,
.T-I-atl:hover,
.TI .T-I-ax7:hover,
.z0 .T-I-ax7:hover,
.G-atb .T-I-ax7:hover,
.T-I-ax7:hover,
#gbqfb:hover {
    background: none;
    background-color: #004851;
    background-image: none;
    border: 1px solid #333;
    box-shadow: none;
    color: #fff;
    cursor: pointer;
    text-shadow: none;
}
}
