css:
@-moz-document domain('medium.com'), domain('hackernoon.com') {
  @media print {
    .graf {
      margin-left: -100px;
      width: 625px;
    }
    
    /* 'body' makes the selectivity just enough */
    body .gist .blob-code {
       line-height: 12px;
    }
    body .gist .blob-code-inner {
       font-size: 9px;
    }
    body .gist .blob-num {
       line-height: 12px;
       font-size: 9px;
    }
  }
}
