css:
@-moz-document regexp("^https?://chat.(meta.)?stack(overflow|exchange).com/.*$") {
#input-area {
    background: #252525 !important;
}

#input {
    color: #aaa !important;
    background-color: #252525 !important;
}

#sidebar {
    color: #aaa !important;
}

#starred-posts li {
    border-bottom: 1px solid #555 !important;
    margin: 5px 0 !important;
    margin-bottom: 0px !important;
}

.catchup-marker {
    border-top-style: none !important;
}

.chat-input textarea {
    background-color: #333 !important;
    color: #bbb !important;
}

.flair, .username {
    color: #999 !important;
}

.mention {
    color: #222 !important;
}

.messages, .timestamp {
    background-color: #222 !important;
    color: #eee !important;
    border: none !important;
    border-radius: 4px;
}

.neworedit {
    background-color: #444  !important;
    color: #bbb !important;
}

.ob-exception-detail {
    background-color: #444 !important;
    color: #ccc !important;
}

.ob-status-text, .ob-blog-meta, .ob-blog-text, .ob-message, .ob-post-body, .ob-exception-info, .ob-exception-type, .ob-exception-time, .ob-exception-server, .ob-exception-url {
    color: #bbb !important;
}

.sidebar-widget * {
    color: #888 !important;
}

.onebox {
    background-color: #444 !important;
    color: #bbb !important;
}

.profiler-results, #debug-message-container, #footer-logo {
    display: none !important;
}

.reply-parent, .reply-child {
    background-color: #666 !important;
    color: #fff !important;
}

.system-message-container .system-message {
    color: #777 !important;
    font-weight: normal !important;
}

.mine .messages {
    background-color: #31516B !important;
}

.content a, .username {
    color: #72B1FF !important;
}

.username.moderator {
    color: #FFB172 !important;
}

body {
    background: none !important;
    background-color: #111 !important;
}

body, textarea {
    font-family: arial !important;
}

.tag {
    color: #3e6d8e !important;
}

a.button {
    color: white !important;
    text-shadow: none !important;
}

.monologue .signature img {
    border-radius: 50%;
    margin: 0 4px;
}

h1#roomtitle {
    text-shadow: none !important;
}

.subheader h1, .reputation-score {
    color: #ccc !important;
}

.popup, .fire-popup {
    background: #222 !important;
    color: #eee !important;
}

.fire-deleted {
    background: #432323 !important;
    margin-top: 4px;
}

.fire-popup pre {
    background: #222 !important;
}

.fire-popup .fire-tooltip {
    background: #222 !important;
}

.message .meta {
    background: none !important;
    color: #eee !important;
}

.ai-feedback-info-tpu {
    color: #88c389 !important;
}

#present-users img {
    width: 16px !important;
    height: 16px !important;
    transition: all 0.2s ease;
}

#present-users:hover img {
    width: 32px !important;
    height: 32px !important;
    transition: all 0.2s ease;
}

#present-users > .present-user, #present-users > .more {
    height: 16px !important;
}

#present-users:hover > .present-user, #present-users:hover > .more {
    height: 32px !important;
}

#present-users .user-container {
    width: 16px !important;
    transition: all 0.2s ease;
}

#present-users:hover .user-container {
    width: 32px !important;
    transition: all 0.2s ease;
}

.sidebar-widget a {
    color: #72B1FF !important;
}

.nav a {
    color: white !important;
}

.roomcard, .roomcard-xxl, .usercard, .usercard-xxl {
    background: #222 !important;
    color: #eee !important;
}
}
