css:
@-moz-document url-prefix("http://forums.kc-mm.com") {
body {
    background: #1a1a1a;
    text-align: center;
    color: #cccccc;
    font-size: 14px;
    font-family: verdana, sans-serif;
    line-height: normal;
}
#wrapper {
    width: 100%;
    min-width: 20%;
    margin: 0 auto;
}
#ustsol {
    background: #1a1a1a;
    padding: 10px;
    text-align: center
}
#footer {
    display: none;
}
#announce {
    display: none;
}
#shameless {
    display: none;
}
#hackHolder {
    overflow-y: auto;
    max-height: 100%;
}
.hackEntry {
    border-style: solid;
    border-radius: 15px;
    background: #1a1a1a;
    border-width: 2px;
    padding: 10px;
    min-height: 180px;
    text-align: left;
}
a:link {
    color: #7777ff;
    text-decoration: none;
    font-family: Helvetica, Arial, FreeSans, sans-serif;
    font-size: 18px;
}
.hackInfo {
    border: 1px solid;
    border-right-width: 2px;
    border-left-width: 2px;
    border-top-style: hidden;
    border-bottom-style: hidden;
    float: none;
    text-align: left;
    padding: 10px;
    margin-left: 10px;
    width: auto;
    height: auto;
    overflow-y: auto;
}
img {
    border-radius: 15px;
    background: #1a1a1a;
    border-width: 1px;
}
}
