@-moz-document domain("stackoverflow.com")
{
    html {
        filter: invert(0.85);
        background: #262626;
    }

    img,
    .-bg-image,
    .so-tag {
        filter: invert(1) contrast(1.3) saturate(1.4);
    }

    #footer {
        filter: invert(1);
    }

    .js-featured-assessments img {
        filter: invert(0);
    }
}
