css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("playoverload.com") {

	#drupalchat ul li div a {
		color: #EDEDED !important;
	}
	#drupalchat .subpanel li {
		background: #444 !important;
	}
	#drupalchat .chatboxcontent .drupalchat_dpic {
		background: #444 !important;
	}
	#drupalchat .chatboxcontent p {
		color: #AAA !important;
	}
	.chatboxinput {
		background: #232323 !important;
	}
	.chatboxinput textarea {
		color: #AAA !important;
	}
	#drupalchat .chatboxcontent {
		background-color: #444 !important;
	}
	#drupalchat #mainpanel .chatboxusername a:hover {
		color: #FE863D !important;
	}
	#drupalchat .subpanel li:hover {
		background: #343434 !important;
	}
	#drupalchat li#chatpanel div.item-list li.role-admin a {
		color: #FE863D !important;
	}
	#drupalchat .chatboxcontent p a,
	.drupalchat-embed-chatroom-container .chatboxcontent p a {
		color: #FE863D !important;
	}
	#drupalchat .chatboxcontent p a:hover,
	.drupalchat-embed-chatroom-container .chatboxcontent p a:hover {
		text-decoration: underline !important;
	}
	div.drupalchat-embed-chatroom-container div.drupalchat-embed-chatroom-content table tbody p a {
		color: #FE863D !important;	
	}
	div.drupalchat-embed-chatroom-container div.drupalchat-embed-chatroom-content table tbody p,
	.drupalchat-embed-chatroom-chatboxtextarea,
	#drupalchat-embed-user-list { 
		color: #DCDCDC !important;
	}
	#drupalchat .subpanel .chat_options a:hover {
		color: #FE863D !important;
	}
	.ui-tooltip-light {
		background: #777 !important;
		border-color: #D2D2D2;
	}
	.drupalchat-usermenu-tooltip .drupalchat-um-1 .drupalchat-um-1-ls .drupalchat-um-1-ls-innr2 {
		color: #DEDEDE;
	}
	.drupalchat-usermenu-tooltip .drupalchat-um-1 .drupalchat-um-1-ls:hover {
		background: #565656;
	}
	#drupalchat .subpanel .drupalchat_search,
	#drupalchat .subpanel .drupalchat_search .drupalchat_searchinput {
		background: #383838 !important;
	}
}
