css:
@-moz-document domain("simplearmory.com") {
/*
	My first OLED friendly skin.
	D.Szymanski
*/

.view-container,.navbar.navbar-default.navbar-fixed-top.ng-scope,.thumbnail{
    background-color: #23232d;
}
body{
    color: #BEBEBE;
	background-color: #2c2c36;
}
.modal-content,.dropdown-menu,.progress,.active,.navbar-default .navbar-nav>.open>a, .navbar-default .navbar-nav>.open>a:focus, .navbar-default .navbar-nav>.open>a:hover{
	background-color: #c1c1c1;
}
.h1, .h2, .h3, .h4, .h5, .h6, h1, h2, h3, h4, h5, h6,.cal td{
    color: #BEBEBE;
}
.navbar-default .navbar-nav>.active>a, .navbar-default .navbar-nav>.active>a:focus, .navbar-default .navbar-nav>.active>a:hover{
	background-color: #BEBEBE;
}
}
