css:
@-moz-document domain("www.komplett.no") {
.navigation-header-main {
    background: #333;
    color: #ccc;
}
.navigation-header {
    background: #333 !important;
}
.navigation-menu ul span {
    color: #ccc !important;
}
.navigation-menu li:hover {
    color: #333 !important;
}
.navigation-header-user-menu__list-item-link {
    background: #555 !important;
    color: #ccc !important;
}
.navigation-header-user-menu__list-item-link:hover {
    background: #ccc !important;
    color: #333 !important;
}
body {
    color: #ccc !important;
}
#MainContent {
    background: #555;
}
.container {
    background: #333 !important;
}
.main-body {
    background: #333 !important;
}
.maincontent-container {
    background: #333 !important;
    color: #ccc;
}
.content-block .text-content {
    color: #ccc !important;
}
.search-view-controls {
    background: #555 !important;
}
.values {
    color: #333;
}
.recommendations-title {
    color: #333;
}
.ubo-content {
    background: #333;
}
.product-price,
.product-price-before {
    color: #ccc !important;
}
.newsletter {
    background: #555 !important;
}
.action-container span {
    color: #ccc !important;
}
.navigation-menu__container .navigation-menu__group {
    background: #555 !important;
}
.navigation-menu__container .navigation-menu__group span {
    color: #333 !important;
}
.wishlist-container {
    background: #333;
}
.wishlist-container a {
    color: #ccc !important;
}
.wishlist-container div {
    color: #ccc !important;
}
footer {
    background: #333 !important;
}
footer .link-collection a {
    color: #ccc !important;
}
.product-technical-details{
    color: #333;
}
.review-row{
    color: #333;
}
}
