css:
@-moz-document url("https://app.roll20.net/editor/") {
.sheet-rolltemplate-5e-shaped .sheet-rt-emote {
    background-color: transparent;
    border: 1px dashed #f0c392;
    color: #9a7b57;
    font-size: 12px;
    font-style: normal;
    font-weight: normal;
    padding: 15px;
    text-align: center;
    margin: 5px 15px;
    border-radius: 5px;
}

.sheet-rolltemplate-5e-shaped .sheet-output {
    font-size: 13px;
}
.sheet-rolltemplate-5e-shaped {
    font-family: "verdana",sans-serif;
}

.sheet- {
    line-height: 150%;
}

.sheet-rolltemplate-5e-shaped .sheet-rt-card {
    padding: 8px;}

.sheet-output-label > span {
    margin-left: 5px;
}

.sheet-rolltemplate-5e-shaped .sheet-rt-title {
    font-family: Century, serif;
}
}
