css:
@-moz-document domain("www.se7ensins.com") {
.sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3, .XenBase .xengallerySideBarContainer .xengallerySideBar .section h3 {
    font-size: 12px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: rgb(70, 70, 70);
    background-color: rgb(70, 70, 70);
    padding-top: 7px;
    padding-bottom: 7px;
    padding-left: 10px;
    margin: -3px -3px 5px;
    border: 1px solid rgb(208, 208, 208);
    border-bottom: 3px solid rgb(104, 159, 16);
    border-top-left-radius: 13px;
    border-top-right-radius: 13px;
    background: rgb(239,239,239);
    background: -moz-linear-gradient(top, rgba(239,239,239,1) 0%, rgba(203,203,203,1) 51%, rgba(195,195,195,1) 100%);
    background: -webkit-linear-gradient(top, rgba(239,239,239,1) 0%,rgba(203,203,203,1) 51%,rgba(195,195,195,1) 100%);
    background: linear-gradient(to bottom, rgba(239,239,239,1) 0%,rgba(203,203,203,1) 51%,rgba(195,195,195,1) 100%);
}
.secondaryContentCollapsed h3 {
    font-weight: lighter;
    font-size: 12px;
    font-family: 'Roboto',sans-serif,Helvetica,Arial,sans-serif;
    color: rgb(70, 70, 70);
    background-color: rgb(70, 70, 70);
    padding-top: 7px;
    padding-bottom: 7px;
    padding-left: 10px;
    margin: -3px -3px 5px;
    border: 1px solid rgb(208, 208, 208);
    border-bottom: 3px solid rgb(104, 159, 16);
    border-top-left-radius: 13px;
    border-top-right-radius: 13px;
    background: rgb(239,239,239);
    background: -moz-linear-gradient(top, rgba(239,239,239,1) 0%, rgba(203,203,203,1) 51%, rgba(195,195,195,1) 100%);
    background: -webkit-linear-gradient(top, rgba(239,239,239,1) 0%,rgba(203,203,203,1) 51%,rgba(195,195,195,1) 100%);
    background: linear-gradient(to bottom, rgba(239,239,239,1) 0%,rgba(203,203,203,1) 51%,rgba(195,195,195,1) 100%);
}
.secondaryContentCollapsed {
    background-color: rgb(255, 255, 255);
    padding: 10px 10px 3px;
    border: 1px solid rgb(208, 208, 208);
    border-radius: 13px;
}
}
