css:
@-moz-document url(http://backpage.com) {
.cat {
    background-color: #272822;
    color: #fff;
}
.cat a:link {
    color: #66d9df !important;
    ;
}
.cat a:visited {
    color: #fd971f;
}
.mainBody {
    background-color: #272822;
    color: #fff;
}
.mainBody a:link {
    color: #66d9df;
}
.mainBody a:visited {
    color: #fd971f;
}
.date {
    background-color: #272822;
    color: #f92672;
    background-image: none;
    border: 1px #fff solid !important;
}
#tlHeader {
    background-color: #292822;
    border-bottom: 1px #fff solid;
}
#community .city {
    color: #a6e22e !important;
}
.comm {
    color: #66d9ef !important;
}
#tlHeader #logo a,
.nav-trigger {
    -webkit-filter: invert(1);
    filter: invert(1);
}
#cookieCrumb {
    color: #fff;
    background-color: #292822;
}
#cookieCrumb a {
    color: #fff;
}
#pageBackground {
    background-color: #292822;
    background: #292822;
}
#topSponsorWrapper,
.sponsors,
#sideSponsorTable {
    display: none !important;
}
.pagination span,
.pagination a {
    background: #292822;
}
#tlFooter {
    background: #292822 !important;
    color: #fff;
}
#tlFooter a {
    color: #66d9ef;
}
a.rssLink {
    background-color: #292822;
    color: #FD971F !important;
}
html {
    min-height: 100vh;
    background-color: #292822;
}
}
