@document domain(google.com),
          domain(google.gr),
          domain(www.google.com),
          domain(www.google.gr),
          domain(www.ebay.co.uk),
          domain(ebay.co.uk),
          domain(www.ebay.com),
          domain(ebay.com),
          domain(www.youtube.com),
          domain(youtube.com) {
  /* CSS rules here apply to:
     - Any page whose URL's host is "google.gr"
     - Any page whose URL's host is "google.com"
     - Any page whose URL's host is "eBay.com"
     - Any page whose URL's host is "eBay.co.uk"
     - Any page whose URL's host is "youtube.com"*/

                      /* mouse over link */
                      a:hover {
    color: #309100 !important;
}
                      /* visited link */
                      a:visited {
    color: #BA0000 !important;
  }
}
