css:
@-moz-document domain("www.netflix.com"){
  
  .nfDropDown.theme-lakira .label  {
    border-color: #888;
    background: transparent;
  }

  .nfDropDown.theme-lakira .label .arrow  {
    border-top-color: #888;
  }

  .profiles-gate-container .profile-icon  {
    border-color: #444;
  }

  .profiles-gate-container li a.focused .profile-icon, .profiles-gate-container li a:focus .profile-icon, .profiles-gate-container li a:hover .profile-icon  {
    border-color: #000;
  }

 body, html, .profiles-gate-container  {
    background: #eee!important;
    color: gray;
  }

  .akira-header.head-opaque  {
    background-color: rgba(255, 255, 255, 1);
  }

  .akira-header  {
    background-image: none!important;
    background-color: rgba(255, 255, 255, 0.9);
    -webkit-box-shadow: inset 0px -1px 0px 0px #ddd;
    -moz-box-shadow: inset 0px -1px 0px 0px #ddd;
    box-shadow: inset 0px -1px 0px 0px #ddd;
  }

  .searchTab span, .akira-header>ul>li>a  {
    color: #777;
    text-shadow: none;
  }

  .profile-button a:hover, .profile-button span:hover  {
    color: inherit;
    border: 1px solid grey;
  }

  .nfDropDown.theme-lakira .label, .list-profiles .profile-gate-label, .profiles-gate-container li a.focused .profile-name, .profiles-gate-container li a:focus .profile-name, .profiles-gate-container li a:hover .profile-name, .lolomoRow.lolomoRow_title_card .rowTitle, .lolomoRow.lolomoRow_title_card:hover a.rowTitle:hover, .service-code:hover  {
    color: #888;
  }

.navigation-tab a {
    color: #111 !important;
}

.navigation-tab a:hover {
    color: #E50914 !important;
}

 .main-header {
    border-bottom: 1px solid #ddd !important;
}

.secondary-navigation a{
    color:#111 !important;
}

.pinning-header .pinning-header-container .main-header {
    background:#ddd !important;
}

.sub-header-wrapper {
    background-color: #eee !important;
}









  
  
  
}
