css:
@-moz-document domain("tweetdeck.twitter.com") {
body {
    font-family: "Comic Sans MS" !important;
    color: #ff7c93;
}

article, aside, details, figcaption, figure, footer, header, hgroup, nav, section, summary {
    border-radius: 100%;
}

header.js-app-header {
    border-radius: 30px;
}

.stream-item {
    border-radius: 20px;
    border: 1px solid;
    margin-bottom: 10px;
}

h1 {
    text-shadow: 0px 0px 3px #ff002d;
    color: #fb7990;
    text-align: center;
}

.tweet-text {
	border-radius: 50%;
    display: table-cell;
    text-align: center;
    vertical-align: middle;
    padding: 1em;
    border: 2px dotted pink;
    background-color: #ffdce2;
}
}
