/* ==UserStyle==
@name	Wikipedia Remove Style
@namespace	me._unweb
@version        0.1.0
@description	Remove CSS from wikipedia.
@author	_unweb
@license	CC0
==/UserStyle== */


@-moz-document domain("wikipedia.org") {
    .mw-body, .parsoid-body, #mw-content {
		font-size: 1rem !important;
		background: -moz-default-background-color !important;
		color: -moz-default-color !important;
	}
	
	body {
		font-size: 1rem !important;
		background: -moz-default-background-color !important;
		color: -moz-default-color !important;
	}
	
	a:hover, a {
		color: -moz-hyperlinktext !important;;
	}
	a:visited {
		color: -moz-visitedhyperlinktext !important;
	}
	
	a:active {
		color: -moz-activehyperlinktext !important;
	}
	
	p {
		font-size: 1rem !important;
		background: -moz-default-background-color !important;
		color: -moz-default-color !important;
	}
	
	.mw-body-content, #mw-page-base {

	/*
	    line-height: 1.6;
	*/
		font-size: 1rem !important;
		background: -moz-default-background-color !important;
		color: -moz-default-color !important;

	}
	
	/* Change font size of reference tooltips to that of normal text */
	.referencetooltip
	{
		font-size: 1rem !important;
	}
}
