css:
@-moz-document domain("fc2.com"){




body, div{
   background: #555 !important;
}

p, span, div, strong{
    color:#ddd !important;
}

a:link{
    color:#fff !important;
}

a:visited{
    color:#aaa !important;
}


.tab li{
    background:#444 !important;
}

#inf_box ul.panel li.p_body {
    background: #555;
}

#menu .menu_body ul#menu_list {
    background: #555 !important;
}






.sh_heading_main_b{
    background:#444 !important;
}

.ad_300x250{display:none !important;}

#inf_box ul.panel li.p_body {
    width: 658px !important;
}



#sh_header_box {
    border-bottom: 1px dotted #777 !important;
    height: 37px;
    top: 35px !important;
    background: none !important;
}

div#menu {
    background: #333 !important;
}

div#menu > div {
    background: #333 !important;
}

#container div#header {
    height: 90px !important;
}

#footer_global, #fc2menu_global, #footer_outline{
    background:#333 !important;
}

#inf_box ul.panel li.p_body {
    border: 1px solid #777 !important;
}

.sh_heading_main_b_wide{
    background: #444 !important;
}

.genre_over_wrapper section{
    background: #777 !important;
}

.sh_menu_list {
    background: #555 !important;
}

#content > div#main > ul.points > li > a{
    color:#555 !important;
}

div#container > div#main > div#left_contents > div.menu_body > ul > li > a{
        color:#555 !important;
}

#left_contents  > div.menu_body > ul > li.icon > ul.sub_menu > li  > a{
	color:#555 !important;    
}

#primary .content_body dd.info {
    background: none !important;
}

#primary .content_body ul.top_request_list {
    background: none !important;
}


#spbar > div#container > div#header > h1{
    background: url("https://s20.postimg.cc/7a3921m1p/fc2_logo.png") no-repeat left top !important;
}

.sh_langselect > ul > li > select > option{
    background:#555 !important;
    color:#fff !important;
}

.sh_langselect > ul > li > select{
    background:#555 !important;
    color:#fff !important;
    border:1px solid #777 !important;
}











}
