{
  .wrapper {
    width: 99%!important;
}
#primary {
    width: 99%!important;
}
#secondary {
    width: 1%!important;
}
.widget-area {
    display: none !important;
    max-width: 1%!important;
}
.socialmedia,
.sharedaddy {
    display: none !important;
}
.below-title-meta {
    display: none !important;
}
body #main #primary #content .entry-content {
    width: 100% !important;
}
.entry-content {
    width: 100% !important;
}
#colophon {
    display: none !important;
}
}
